import React, { useEffect, useState } from "react";

export default function Test9() {
    const [count, setCount] = useState(new Date());
    const [age, setAge] = useState(0);
    const [isTimerActive, setIsTimerActive] = useState(true);
    let timer = null;

    useEffect(() => {
        console.log("componentDidmount");
    }, []);

    useEffect(() => {
        console.log("componentDidrender");
    });

    useEffect(() => {
     
        
            timer = setInterval(() => {
                console.log("componentDidUpdate");
                setCount(new Date());
            }, 900);
     

        // Fonction de nettoyage pour arrêter l'intervalle quand le composant se démonte
        return () => {
            if (timer) {
                clearInterval(timer);
                console.log("Timer cleared");
            }
        };
    }, [age, isTimerActive]);

    return (
        <>
            <p>Âge: {age}</p>
            <p>Date: {count.toString()}</p>
            <button
                onClick={() => {
                    setAge((prevState) => prevState + 1);
                   
                    // Arrête le timer quand on clique
                }}
            >
                Click to stop timer
            </button>
        </>
    );
}
