import React, { createContext, useState } from "react";
import Read from "./read";
import Create from "./create";
import Layout from "./route_layout";

export const UsersContext = createContext({
  usersd: [],
  lastid: 0,
});

export default function App() {
  const [usersd, setusersd] = useState([
    { fullname: "John Doe", country: "USA", id: 1 },
    { fullname: "Jane Smith", country: "France", id: 2 },
    { fullname: "Ahmed Ali", country: "Maroc", id: 3 },
  ]);
  const [lastid, setlastid] = useState(0);

  const onaddusers = (newUser) => {
    setlastid(newUser.id);
    setusersd([...usersd, newUser]);
  };

  const updateuser = (changeUser) => {
    console.log("testing de data de update");
    setusersd(
      usersd.map((user) =>
        user.id === changeUser.id ? { ...user, ...changeUser } : user
      )
    );
  };

  return (
    <>
      <UsersContext.Provider
        value={{
          usersd: usersd,
          lastid: lastid,
          onaddusers: onaddusers,
          updateuser: updateuser,
        }}
      >
        <Layout />
      </UsersContext.Provider>
    </>
  );
}
