import React, { useEffect, useState, useRef } from "react";

export default function Test9() {
  const [count, setCount] = useState(new Date());
  const intervalIdRef = useRef(null); // Utilisation de useRef pour stocker l'ID de l'intervalle

  useEffect(() => {
    intervalIdRef.current = setInterval(() => {
      console.log("componentDidUpdate");
      setCount(new Date());
    }, 900);

    // Nettoyage de l'intervalle lors du démontage du composant
    return () => clearInterval(intervalIdRef.current);
  }, []);

 

  return (
    <>
      {count.toString()}
      <button onClick={addMinuteAndStopInterval}>
        Click (Add 1 Minute and Stop Interval)
      </button>
    </>
  );
}
