import React, { useRef, useState } from "react";

export default function Create({ onaddusers }) {
  const fullname = useRef();
  const country = useRef();
  const id = useRef();
  const [data, setdata] = useState([]);
  const [currentId, setCurrentId] = useState(0); // Gérer l'ID avec useState

  const handleform = () => {
    if (data.length > 0) {
      onaddusers(data);
    } else {
      console.log("Affichage: no data");
    }
  };

  const handlesubmit = (e) => {
    e.preventDefault();

    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;

    // Incrémentation de l'ID à chaque soumission
    const newId = currentId + 1;

    setdata((prev) => [
      ...prev,
      {
        fullname: fullnamedata,
        country: countrydata,
        id: newId,
      },
    ]);

    setCurrentId(newId); // Mettre à jour l'ID actuel

    handleform();

    // Réinitialiser le formulaire
    fullname.current.value = "";
    country.current.value = "maroc"; // Réinitialiser la sélection par défaut
  };

  return (
    <>
      <form onSubmit={handlesubmit}>
        <label htmlFor="id">ID</label>
        <input
          type="text"
          readOnly
          id="id"
          value={currentId + 1} // Afficher l'ID suivant
        />
        <label htmlFor="fullname">Fullname</label>
        <input
          type="text"
          name="fullname"
          id="fullname"
          ref={fullname}
          required
        />
        <label htmlFor="country">Country</label>
        <select name="country" id="country" ref={country}>
          <option value="maroc">Maroc</option>
          <option value="france">France</option>
          <option value="espane">Espagne</option>
          <option value="usa">USA</option>
        </select>

        <button type="submit">Submit</button>
      </form>
    </>
  );
}
