import React, { createContext, useContext, useEffect, useRef, useState } from "react";
import { UsersContext } from "./apk";
import { Link } from "react-router-dom";


export default function Create({lastid=0,onaddusers}){

const fullname=useRef()
const country= useRef()

const context = useContext(UsersContext);

    useEffect(() => {
        console.log("testing de context", context.lastid
        );
    }, [context]);
const handlesubmit = (e) => {
    e.preventDefault();
   
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    const iddata =  context.lastid + 1 ;
    const newUser = { fullname: fullnamedata, country: countrydata, id: iddata, };

    context.onaddusers(newUser);
    
  
};

    return(
        <>
        <form action="" onSubmit={handlesubmit} className="container mt-4 " >
           <div className="mb-3"> <label htmlFor=""  className="form-label">id</label>
             <input type="text" readOnly name="" id="" value={ context.lastid +1} className="form-control"/>
             </div>
             <div className="mb-3">
            <label htmlFor="" className="form-label">fullname</label>
            <input type="text" name="fullname" id="fullname" ref={fullname} className="form-control" />
            </div>
            <div className="mb-3">
            <label htmlFor="" className="form-label">country</label>
            <select name="country" id="country" ref={country} className="form-select">
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            </div>
            
            <button type="submit" className="btn btn-primary" >
                
                 </button>
        </form>
        </>
    )
}
