import { type } from "@testing-library/user-event/dist/type"
import { connect } from "react-redux"
import { Counter_Decrement, Counter_Increment, Counter_Reset } from "./counterRedux"
import { CounterSelecteur } from "./counterSelecteur"

export default function Counter({counter,onIncrement, onDecrement ,onReset}){

    const Increment =()=>{
       
        onIncrement()
    }
    const Decrement =()=>{
        onDecrement()
    }
    const Reset =()=>{
        onReset()
    }
    return(
        <>
        <div>
            <span>{counter}</span>
            <button type="button" className="btn btn-primary" onClick={Increment}>Increment</button>
            <button type="button" className="btn btn-primary" onClick={Decrement}>Decrement</button>
            <button type="button" className="btn btn-primary" onClick={Reset}>Reset</button>
        </div>
        </>
    )
}


const mapStateToProps = (state) => ({
  counter: CounterSelecteur,
});
const mapDispatchToProps = (dispatch) => ({
  
    onIncrement: value => dispatch({ type:Counter_Increment , payload:(value) }),
    onDecrement: value => dispatch({ type: Counter_Decrement , payload:(value)}),
    onReset: value => dispatch({ type: Counter_Reset, payload: { value: 0 } }),
  });
  
export const CounterStore = connect(mapStateToProps,mapDispatchToProps)(Counter)
