import React, { useEffect, useState } from "react";
import { UseFetch } from "./useFetch";

export default function CustomHooks() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  // Fetch data using the custom hook
  const productData = UseFetch("https://fakestoreapi.com/products");
  const categoriesData = UseFetch("https://fakestoreapi.com/products/categories");

  // Update state when data is fetched
  useEffect(() => {
    if (productData) {
      setProducts(productData);
    }
    if (categoriesData) {
      setCategories(categoriesData);
    }
  }, [productData, categoriesData]);

  // Filter products based on search input and selected category
  const filteredProducts = products.filter(
    (product) =>
      (product.id.toString().startsWith(searchInput) ||
        product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
        product.price.toString().startsWith(searchInput) ||
        product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
        product.category.toLowerCase().startsWith(searchInput)) &&
      (selectedCategory === "" || product.category === selectedCategory)
  );

  return (
    <>
      <h1>Custom Hooks</h1>

      <div className="container-fluid ms-auto w-75">
        <h2>Search</h2>
        <form>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search products..."
              value={searchInput}
              onChange={(e) => setSearchInput(e.target.value)}
            />
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              {categories.map((category, index) => (
                <option key={index} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>
        </form>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Price</th>
              <th>Description</th>
              <th>Category</th>
              <th>Image</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts.map((product) => (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td>{product.title}</td>
                <td>${product.price}</td>
                <td>{product.description}</td>
                <td>{product.category}</td>
                <td>
                  <img src={product.image} alt={product.title} width="50" />
                </td>
                <td>{product.rating?.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
