import React, { useEffect, useState } from "react";
import { UseFetch } from "./useFetch";
import Production from "../partie1/p10/production";

export default function CustomHooks() {
    const [productListe, setProductListe] = useState([]);
    const [searchInput, setSearchInput] = useState("");
    const [categoryListe, setCategoryListe] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("");

    // Fetch product and category data using UseFetch
    const products = UseFetch("https://fakestoreapi.com/products");
    const categories = UseFetch("https://fakestoreapi.com/products/categories");

    useEffect(() => {
        if (products) {
            setProductListe(products);
        }
        if (categories) {
            setCategoryListe(categories);
        }
    }, [products, categories]);

    // Handle category button click
    const handleCategoryClick = (category) => {
        setSelectedCategory(category);
    };

    // Display category buttons dynamically
    const displayCategories = () => {
        return categoryListe.length > 0
            ? categoryListe.map((category, index) => (
                  <button
                      className="btn btn-secondary"
                      key={index}
                      onClick={() => handleCategoryClick(category)}
                  >
                      {category}
                  </button>
              ))
            : null;
    };

    // Filter products based on search input and selected category
    const filteredProducts = productListe.filter((product) => {
        const matchesSearch = (
            product.id.toString().startsWith(searchInput) ||
            product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
            product.price.toString().startsWith(searchInput) ||
            product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
            product.category.toLowerCase().startsWith(searchInput.toLowerCase())
        );
        const matchesCategory = selectedCategory
            ? product.category === selectedCategory
            : true;

        return matchesSearch && matchesCategory;
    });

    // Handle form submission
    const handleSearch = (e) => {
        e.preventDefault();
        const searchData = e.target.search.value;
        setSearchInput(searchData);
    };

    return (
        <div className="container-fluid ms-auto w-75">
            <h2>Search</h2>
            <form onSubmit={handleSearch}>
                <div className="form-group">
                    <input
                        type="text"
                        name="search"
                        id="search"
                        onChange={(e) => setSearchInput(e.target.value)}
                    />
                    <input type="submit" value="Search" id="submit" />
                    <hr />
                    <h5 className="title">Category:</h5>
                    <div className="btn-group">{displayCategories()}</div>
                </div>
            </form>

            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Price</th>
                        <th>Description</th>
                        <th>Category</th>
                        <th>Image</th>
                        <th>Rating</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredProducts.length > 0 ? (
                        filteredProducts.map((product, key) => (
                            <Production productData={product} key={key} />
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7">No products found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
}
