import React, { useCallback, useState } from "react";

export default function UseCallbackExample() {
    const [isRendered, setIsRendered] = useState(false);

    // Définition des constantes
    const a = 2;
    const b = 2;

    // Fonction optimisée avec useCallback
    const calculateSum = useCallback(() => {
        const sum = a + b;
        console.log("Sum calculated:", sum);
        return sum;
    }, [a, b]);

    // Fonction simple sans useCallback
    const logMessage = () => {
        console.log("This is not using useCallback.");
    };

    // Mise à jour de l'état
    const triggerRender = () => {
        setIsRendered(true);
        console.log("Rendered state set to true.");
    };

    return (
        <>
            <input
                type="button"
                value="Calculate Sum"
                onClick={calculateSum}
            />

            <input
                type="button"
                value="Log Message"
                onClick={logMessage}
            />

            <input
                type="button"
                value="Trigger Render"
                onClick={triggerRender}
            />

            {/* Afficher l'état pour vérifier le changement */}
            {isRendered && <p>The component has been rendered!</p>}
        </>
    );
}
