import { useSelector } from "react-redux";
import { UsersSelectors } from "./Selectors/UsersSelectors";

export default function User() {
 const users = useSelector(UsersSelectors);

 // Define a mapping for the country flags based on their code
 const flagUrl = {
   maroc: 'https://flagcdn.com/w320/ma.png', // URL for Morocco flag
   // Add more countries as needed...
 };

 return (
   <div className="container mt-4">
     <div className="card">
       <div className="card-body text-center">
         <h5 className="card-title">{users.name}</h5>
         {/* Displaying country flag */}
         {users.country && (
           <div className="d-flex align-items-center justify-content-center mb-2">
             <img 
               src={`https://flagcdn.com/w320/$.png`} 
               alt={`${users.country} flag`} 
               style={{ width: '40px', height: 'auto' }} 
             />
             <span className="ms-2">{users.country}</span>
           </div>
         )}
         {/* Displaying age */}
         <p className="card-text">Age: {users.age}</p>

         {/* Buttons Section */}
         <div className="mt-3">
           <button type="button" className="btn btn-primary me-2">Update</button>
           <button type="button" className="btn btn-secondary">Reset</button>
         </div>
       </div>
     </div>
   </div>
 );
}
