import { connect, useDispatch, useSelector } from "react-redux"

import { CounterSelecteur } from "./counterSelecteur"
import { action_Decrement, action_Increment, action_Reset } from "./counteraction"

export  function Counter({Increment ,Decrement , Reset ,selecteur}){


    return(
        <>
        <div>
            <span>{selecteur}</span>
            <button type="button" className="btn btn-primary" onClick={Increment}>Increment</button>
            <button type="button" className="btn btn-primary" onClick={Decrement}>Decrement</button>
            <button type="button" className="btn btn-primary" onClick={Reset}>Reset</button>
        </div>
        </>
    )
};

/*
const mapStateToProps = (state) => ({
  counter: CounterSelecteur(state),
});
const mapDispatchToProps = (dispatch) => ({
  
    onIncrement: value => dispatch(action_Increment()),
    onDecrement: value => dispatch(action_Decrement()),
    onReset: value => dispatch(action_Reset()),
  });
  
export const CounterStore = connect(mapStateToProps,mapDispatchToProps)(Counter)
  
*/

export default function CounterStore(){
  const dispatch = useDispatch()
  const selecteur = useSelector(CounterSelecteur)
  
  
  const Increment = ()=> { dispatch(action_Increment())}
      
  const Decrement  = ()=>   {dispatch(action_Decrement())}
      
  const Reset = ()=> {dispatch(action_Reset())}
    
  return(
    <>
    <Counter onIncrement={Increment} onDecrement={Decrement}  onReset={Reset} onselecteur={selecteur} />
    </>
  )
}
